<template>

  <div class="cartGoodsItem">
    <div class="isCheckItem" @click="checkeds">
      <img :src="checkedflag" alt />
    </div>
    <div class="goodsImg">
      <img :src="items.list_pic_url" alt />
    </div>
    <div class="cartGoodsMsg" v-if="!compileflag">
      <div>{{items.goods_name}}</div>
      <div></div>
      <div style="color: red;">￥{{items.retail_price}}</div>
    </div>
    <div class="cartGoodsNum" v-if="!compileflag">X{{items.number}}</div>
    <div class="cartGoodEditWrap" v-if="compileflag">
        <div class="cartEditSizeName">已选择：</div>
        <div class="cartEditNum">
          <div style="color: red;">￥{{items.retail_price}}</div>
          <div>
              <CountOp :items="items"/>
          </div>
        </div>
    </div>
  </div>
</template>
<script>
import "./css/cartGoodsItem.css";
import CountOp from './countOp'
import { mapState, mapMutations, mapActions } from "vuex";
export default {
  props: {
    Checked: {
      type: Number
    },
    compile: {
      type: Boolean
    },
    items:{
      type:Object
    }
    
  },
  components: {
      CountOp
  },
  data() {
    return {
      flag: true,
      checkedflag:"",
      compileflag: false
    };
  },
  computed: {
    ...mapState("shopcar", {
      checked: "checked",
      cartlist:"cartlist"
    })
  },
  methods: {
    ...mapActions("shopcar", ["getDelete",'getChecked','getGoodscount']),
    ...mapMutations("shopcar", ["setflag",]),
    checkeds() {
      this.flag = !this.flag;
      this.items.checked= this.items.checked===0?1:0;
      this.getChecked({isChecked:this.items.checked,productIds:this.items.product_id})
      this.setflag()
    }
  },
  created() {
    this.checkedflag =this.items.checked == 0
          ? "data:image/png;base64,"
        : "data:image/png;base64,";
  },
  mounted() {},
  watch: {
    checked() {
      // this.items.checked = this.checked;
     
       this.checkedflag =this.items.checked == 0
          ? "data:image/png;base64,"
        : "data:image/png;base64,";
    },
    items(){
      this.checkedflag =this.items.checked == 0
          ? "data:image/png;base64,"
        : "data:image/png;base64,";

    },
    compile(){
 this.compileflag = this.compile;
    }
  }
};
</script>
